<?php
 namespace blobfolio\wp\looksee\vendor\common; class cli { public static function is_cli() { return ('cli' === php_sapi_name()); } public static function is_root() { if (function_exists('posix_getuid')) { return (0 === posix_getuid()); } return false; } public static function colorize($args) { $args = func_get_args(); $out = ''; foreach ($args as $v) { ref\cast::to_array($v); if (!count($v)) { continue; } $str = array_shift($v); ref\cast::to_string($str, true); ref\format::array_flatten($v); $codes = array_filter($v, 'is_numeric'); if (count($codes)) { ref\cast::to_int($codes); $out .= "\033[" . implode(';', $codes) . "m{$str}\033[0m"; } else { $out .= $str; } } return $out; } } 